echo -e "\033[1;35m│\033[0m                                                                              \033[1;35m│\033[0m"
echo -e "\033[1;35m│\033[0m  \033[2m$(date -d '5 seconds ago' +%H:%M:%S 2>/dev/null || date +%H:%M:%S)\033[0m  \033[1;33m▸ agent.heartbeat\033[0m     \033[1;31mLUCIDIA\033[0m   \033[2m{alive: true}\033[0m          \033[1;35m│\033[0m"
echo -e "\033[1;35m│\033[0m  \033[2m$(date +%H:%M:%S)\033[0m  \033[1;36m▸ task.assigned\033[0m       \033[1;36mALICE\033[0m     \033[2m{id: #f2a1}\033[0m            \033[1;35m│\033[0m"
