echo -e "\033[1;35m│\033[0m  \033[2m$(date +%H:%M:%S)\033[0m  \033[1;35m▸ memory.write\033[0m        \033[1;35mECHO\033[0m      \033[2m{chunks: 12}\033[0m           \033[1;35m│\033[0m"
